mapper.select:
select	*
from	account_list
where	user_num = #{user_num} || busi_num = #{busi_num}

mapper.update:
update account_list
set deposit = #{deposit}
where user_num = #{user_num} || busi_num = #{busi_num}

update project_list
set current_price = #{current_price}
where project_num = #{project_num}
