mapper.select:
select * 
from user_list
where email=#{email} and user_password=#{user_password}

select * 
from busi_user_list
where busi_resi_num=#{busi_resi_num} and busi_password=#{busi_password}

select count(*) 
from user_list 
where email=#{email}

select count(*) 
from busi_user_list 
where manager_email=#{manager_email}

mapper.update:
update user_list
set user_password = #{newPassword} 
where email = #{email}

update user_list
set cer_number = #{newPassword} 
where email = #{email}

mapper.select:
select manager_name
from busi_user_list
where manager_email = #{manager_email}

select user_name
from user_list
where email = #{email}

select email
from user_list
where user_name = #{user_name}

